mapper.insert:
INSERT INTO codeGroup (
    cgName
    , cgNameEng
    , delNy
    , useNy
) VALUES (
    #{cgName}
    , #{cgNameEng}
    , #{useNy}
    , #{useNy}
)

	SELECT last_insert_id()

mapper.update:
UPDATE 
	codeGroup a
SET 
	a.cgName = #{cgName}
	, a.cgNameEng = #{cgNameEng}
	, a.useNy = #{useNy}
	, a.delNy = #{delNy}
	, a.updatedAt = now() 
WHERE cgSeq = #{cgSeq};
