mapper.insert:
INSERT INTO codeGroup (
	cgSeq
    , cgName
    , cgNameEng
    , delNy
    , useNy
) VALUES (
	#{cgSeq}
    , #{cgName}
    , #{cgNameEng}
    , #{delNy}
    , #{useNy}
)

	SELECT last_insert_id()

mapper.update:
UPDATE 
	codeGroup a
SET 
	a.cgSeq = #{cgSeq}
	, a.cgName = #{cgName}
	, a.cgNameEng = #{cgNameEng}
	, a.useNy = #{useNy}
	, a.delNy = #{delNy}
	, a.updatedAt = now() 
WHERE seq = #{seq};
