mapper.update:
UPDATE user
SET 
    phone = #{phone}
    , emailFull = CONCAT(#{email},'@',(SELECT cc.ccNameEng FROM commonCode cc WHERE cc.seq = #{domain}))
    , postcode = #{postcode}
    , address = #{address}

		,password = #{pwnew}

WHERE 1=1
	AND seq = #{seq}
	AND password = #{pwnow}

UPDATE ${tableName}
SET path = #{path},
	originalName = #{originalName},
	uuidName = #{uuidName},
	ext = #{ext},
	size = #{size},
	delNy = 0,
	pseq = #{pseq}
